<script>
jQuery(document).ready(function() {
    jQuery('#cancel').hide();

    function highlightText() {
        var searchValue = jQuery('#search_field').val().trim(); // Get the search input value
        var regex = new RegExp(searchValue, 'gi'); // Create a regular expression for the search term

        jQuery('.content-box').each(function() {
            var contentElement = jQuery(this); // Use the current .content-box element
            var originalHtml = contentElement.data('original-html') || contentElement.html(); // Get the original HTML

            // Store original HTML if not already stored
            if (!contentElement.data('original-html')) {
                contentElement.data('original-html', originalHtml);
            }

            // Function to highlight text nodes
            function highlightTextNodes(element) {
                element.contents().each(function() {
                    if (this.nodeType === 3) { // Text node
                        var text = this.nodeValue;
                        if (searchValue) {
                            var highlightedText = text.replace(regex, function(match) {
                                return '<span class="highlight">' + match + '</span>';
                            });
                            jQuery(this).replaceWith(highlightedText);
                        }
                    } else if (this.nodeType === 1) { // Element node
                        highlightTextNodes(jQuery(this));
                    }
                });
            }

            // Highlight text in the content element
            if (searchValue !== '') {
                var tempDiv = jQuery('<div>').html(originalHtml); // Use a temporary div to handle the HTML content
                highlightTextNodes(tempDiv);
                contentElement.html(tempDiv.html()); // Replace content with highlighted HTML
            } else {
                contentElement.html(originalHtml); // Restore the original HTML if search value is empty
            }
        });
    }

    jQuery('#search_field').on('keyup', function() {
        var value = jQuery(this).val().toLowerCase(); // Convert input to lowercase for case-insensitive search

        if (value) { 
            jQuery('#cancel').show();
        } else {
            jQuery('#cancel').hide();
        }

        jQuery('.content-box').each(function() {
            var row = jQuery(this);
            var text = row.text().toLowerCase(); // Convert row text to lowercase

            if (text.indexOf(value) >= 0 || value === "") {
                row.show(); // Show rows that match or if search input is empty
            } else {
                row.hide(); // Hide rows that don't match
            }
        });

        highlightText(); // Call highlightText after showing/hiding elements
    });

    jQuery('#cancel').on('click', function() {
        jQuery('#search_field').val('').trigger('keyup'); // Clear the input field and trigger keyup event
        jQuery(this).hide(); // Hide the cancel button
    });
});
</script>

